import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { PhoneBookEntry } from '../models/phone-book-entry';

@Component({
  selector: 'app-phone-book',
  templateUrl: './phone-book.component.html',
  styleUrls: ['./phone-book.component.css']
})
export class PhoneBookComponent {
  entries: PhoneBookEntry[] = [];

  constructor(
    private http: HttpClient,
    @Inject('BASE_URL') private baseUrl: string
  ) {
    this.getEntries();
  }

  save() {
    this.http.post<PhoneBookEntry>(`${this.baseUrl}phonebook`, { phonebookEntryId: 1, firstname: 'John', surname: 'Doe', phoneNumber: '123456789' })
      .subscribe(result => {
        this.getEntries();
      });
  }

  getEntries() {
    this.http.get<PhoneBookEntry[]>(`${this.baseUrl}phonebook`)
      .subscribe(result => {
        this.entries = result;
      }, error => console.error(error));
  }
}
